mapper.select:
select *  from page_message
where is_del = 0

  and userId = #{userId}

  and number = #{number}

limit #{pageNum},#{pageSize}

select count(*) as num from page_message
where userId = #{userId} and is_del = 0

    and id = #{id}


    and materialID = #{materialID}


    and areaID = #{areaID}


    and statusID = #{statusID}
