mapper.select:
SELECT COUNT(*) FROM page_report a WHERE a.statusID = "qy"
AND a.type = #{type}
AND (a.type = '1' or (a.type = '2' and a.userID = #{userID}))
AND a.name LIKE CONCAT('%', #{title}, '%')
AND a.userID = #{userID}

mapper.update:
update page_repor_remark
set mark = #{mark},
    updateTime = #{updateTime}
where id =#{id}

mapper.select:
select * from page_repor_remark
where reportId = #{reportId}
and userId = #{userId}

SELECT a.mid AS "mId", a.area AS "areaId", ANY_VALUE(a.mat_name) AS "maName", ANY_VALUE(a.area_name) AS "areaName", ANY_VALUE(a.price) AS "price", ANY_VALUE(a.price) / (ANY_VALUE(a.tongbi) + 1) AS "tbPrice", ANY_VALUE(a.price) / (ANY_VALUE(a.huanbi) + 1) AS "hbPrice", ANY_VALUE(a.exponent) AS "exponent", ANY_VALUE(a.huanbi) AS "hb", ANY_VALUE(a.tongbi) AS "tb", b.munit AS "munit", ANY_VALUE(a.mdate) AS "maDate", ANY_VALUE(a.`level`) AS "levle", c.pid AS "pId" FROM page_material_pice a LEFT JOIN hl_main_material b on b.id = a.mid LEFT JOIN hlidx_category c ON c.id = a.mid WHERE a.is_del = '0'
AND a.type = #{type}
AND FIND_IN_SET(a.area,#{contrastRegionID})
AND FIND_IN_SET(a.mid,#{materialClassID})
and month (mdate) = #{timeIntervalMonth} and year(mdate) = #{timeIntervalYear}
and quarter(mdate) = #{timeInterval}
and year(mdate) = #{timeInterval}
GROUP BY mid,area
