mapper.select:
SELECT a.id AS "id", a.`name` AS "title", a.type AS "type", a.dataType as "dataType", a.userID, a.createTime AS "createTime", a.materialClassID as "materialClassID", a.contrastRegionID as "contrastRegionID", group_concat(DISTINCT b. NAME) AS "materialName", group_concat(DISTINCT c. NAME) AS "areaName" FROM page_report a LEFT JOIN hlidx_category b ON find_in_set(b.id,a.materialClassID) LEFT JOIN hlidx_areas c ON find_in_set(c.id,a.contrastRegionID) WHERE a.statusID = "qy"
AND a.type = #{type}
AND (a.type = '1' or (a.type = '2' and a.userID = #{userID}))
AND a.name LIKE CONCAT('%', #{title}, '%')
AND a.userID = #{userID}
group by id ORDER BY createTime DESC LIMIT #{pageCurrent},#{pSize}

SELECT COUNT(*) FROM page_report a WHERE a.statusID = "qy"
AND a.type = #{type}
AND (a.type = '1' or (a.type = '2' and a.userID = #{userID}))
AND a.name LIKE CONCAT('%', #{title}, '%')
AND a.userID = #{userID}

mapper.update:
update page_repor_remark
set mark = #{mark},
    updateTime = #{updateTime}
where id =#{id}

mapper.select:
select * from page_repor_remark
where reportId = #{reportId}
and userId = #{userId}

SELECT a.mid as "mId", a.area AS "areaId", a.mat_name AS "maName", a.area_name AS "areaName", a.price AS "price", a.price/(a.tongbi + 1) AS "tbPrice", a.price/(a.huanbi + 1) AS "hbPrice", a.exponent AS "exponent", a.huanbi AS "hb", a.tongbi AS "tb", b.munit AS "munit", a.mdate as "maDate" FROM page_material_pice a LEFT JOIN hl_main_material b on b.id = a.mid WHERE a.type = #{type} AND FIND_IN_SET(a.area,#{contrastRegionID}) AND FIND_IN_SET(a.mid,#{materialClassID})
and month (mdate) = #{timeIntervalMonth} and year(mdate) = #{timeIntervalYear}
and quarter(mdate) = #{timeInterval}
and year(mdate) = #{timeInterval}
