mapper.select:
SELECT a.id AS "id", a.`name` AS "title", a.type AS "type", a.dataType as "dataType", a.userID, a.createTime AS "createTime", b.`name` as "materialName" FROM page_report a LEFT JOIN hlidx_category b ON a.materialClassID = b.id WHERE a.statusID = "qy"
AND a.type = #{type}
AND (a.type = '1' or (a.type = '2' and a.userID = #{userID}))
AND a.name LIKE CONCAT('%', #{title}, '%')
AND a.userID = #{userID}
ORDER BY createTime DESC LIMIT #{pageCurrent},#{pSize}

SELECT COUNT(*) FROM page_report a WHERE a.statusID = "qy"
AND a.type = #{type}
AND (a.type = '1' or (a.type = '2' and a.userID = #{userID}))
AND a.name LIKE CONCAT('%', #{title}, '%')
AND a.userID = #{userID}

mapper.update:
update page_repor_remark
set mark = #{mark},
    updateTime = #{updateTime}
where reportId =#{reportId}
and userId =#{userId}

mapper.select:
select * from page_repor_remark
where reportId = #{reportId}
and userId = #{userId}
